}
// Styled components ....
const StyledSelect = styled.select`
    color: var(--blue);
  `;

const StyledErrorMessage = styled.div`
    font-size: 12px;
    color: var(--red-600);
    width: 400px;
    margin-top: 0.25rem;
    &:before {
      content: "❌ ";
      font-size: 10px;
    }
    @media (prefers-color-scheme: dark) {
      color: var(--red-300);
    }
  `;

const StyledLabel = styled.label`
    margin-top: 1rem;
  `;

IMANewSaleForm.getInitialProps = async ctx => {
  const res = await fetch('http://localhost:3000/api/inventory')
  const json = await res.json()
  console.log(`Show data fetched. ${JSON.stringify(json)}`);
  var productList = json
  return { productList: productList };
}
const IMANewSaleForm = props => {

  return (
    <>
      <h1>Add New Sale</h1>
      <Formik
        initialValues={{
          customerName: "",
          date: "",
          customerEmail: "",
          quantities: [{}] // added for our select
        }}
        validationSchema={Yup.object({
          customerName: Yup.string()
            .max(15, "Must be 15 characters or less")
            .required("Required"),
          date: Yup.date()
            .required("Required"),
          customerEmail: Yup.string()
            .email("Invalid email address"),
          quantities: Yup.array()
            .of(
              Yup.object().shape({
                name: Yup.string()
                  .required('Required'),
                qty: Yup.number()
                  .min(0, 'Must be atleast 1')
                  .required('Required'),
              })
            ).required('Must have products sold')
            .min(1, 'Minimum of 1 product'),
        })}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            submitNewSale(values);
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ values, errors, touched, handleReset }) => {
          return (
            <Form>
              <MyTextInput
                label="Customer Name"
                name="customerName"
                type="text"
                placeholder="John Doe"
              />
              <MyDateInput
                label="Date of Sale"
                name="date"
                type="date"
                placeholder={moment()}
              />
              <MyTextInput
                label="Customer Email Address"
                name="customerEmail"
                type="email"
                placeholder="jane@doe.com"
              />
              <h3>Add Products Bought</h3>
              <FieldArray
                name="quantities"
                render={arrayHelpers => (
                  <div>
                    {values.quantities.length > 0 &&
                      values.quantities.map((product, index) => (
                        <div className="row" key={index}>
                          <div className="col">
                            <label htmlFor={`quantities.${index}.name`}>Name</label>
                            <MyTextInput
                              name={`quantities.${index}.name`}
                              placeholder="Iron Sheet"
                              type="text"
                            />
                          </div>
                          <div className="col">
                            <label htmlFor={`quantities.${index}.qty`}> Quantity</label>
                            <MyTextInput
                              name={`quantities.${index}.qty`}
                              placeholder="99999"
                              type="number"
                            />
                          </div>
                          <div className="col">
                            <button
                              className="secondary"
                              type="remove"
                              onClick={() => arrayHelpers.remove(index)}
                            >
                              X
                      </button>
                          </div>
                        </div>
                      ))}
                    <button
                      type="add"
                      className="secondary"
                      onClick={() => arrayHelpers.push({ name: "", qty: "" })}
                    >
                      Add Product
              </button>
                  </div>
                )}
              />

              <button type="submit">Submit</button>
            </Form>
          )
        }}
      </Formik>
    </>
  );
};

export default IMANewSaleForm;
