import nextConnect from 'next-connect';
import moment from 'moment'
import middleware from '../../middleware/database';

const handler = nextConnect();

handler.use(middleware);

handler.get(async (req, res) => {
    // console.log(JSON.stringify(req));

    var startDateExists = true;
    var dbQuery = {};
    if (startDateExists) {
        //if date greater than filter is here from index
        var startDateFilter = moment.subtract(1, 'month');
        dbQuery = {
            "date": {
                $gte: new Date(startDateFilter._d)
            }
        };
    }

    let doc = await req.db.collection('sales').find(dbQuery).toArray();
    console.log(doc);
    res.json(doc);
    res.end();
});

export default handler;
