import { Formik, Form, useField, FieldArray } from "formik";
import * as Yup from "yup";
import styled from "@emotion/styled";

var moment = require('moment')
moment().format('L');


const MyTextInput = ({ label, ...props }) => {
  // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
  // which we can spread on <input> and alse replace ErrorMessage entirely.
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input className="text-input" {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ) : null}
    </>
  );
};

const MyProductInput = ({ label, ...props }) => {
  // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
  // which we can spread on <input> and alse replace ErrorMessage entirely.
  const [field, meta] = useField(props);
  return (
    <>
      <div className="col">
        <label htmlFor={`quantities.${index}.name`}>Name</label>
        <MyTextInput
          label={'Name'}
          name={`name`}
          placeholder="Iron Sheet"
          type="text"
        />
      </div>
      <div className="col">
        <label htmlFor={`quantities.${index}.qty`}> Quantity</label>
        <MyTextInput
          label={'Quantity'}
          name={`qty`}
          placeholder="99999"
          type="number"
        />
      </div>
    </>
  );
};

const MyDateInput = ({ label, ...props }) => {
  // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
  // which we can spread on <input> and alse replace ErrorMessage entirely.
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input className="text-input" {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ) : null}
    </>
  );
};

// Styled components ....
const StyledSelect = styled.select`
    color: var(--blue);
  `;

const StyledErrorMessage = styled.div`
    font-size: 12px;
    color: var(--red-600);
    width: 400px;
    margin-top: 0.25rem;
    &:before {
      content: "âŒ ";
      font-size: 10px;
    }
    @media (prefers-color-scheme: dark) {
      color: var(--red-300);
    }
  `;

const StyledLabel = styled.label`
    margin-top: 1rem;
  `;

const MySelect = ({ label, ...props }) => {
  // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
  // which we can spread on <input> and alse replace ErrorMessage entirely.
  const [field, meta] = useField(props);
  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <StyledSelect {...field} {...props} />
      {meta.touched && meta.error ? (
        <StyledErrorMessage>{meta.error}</StyledErrorMessage>
      ) : null}
    </>
  );
};

const IMANewSaleForm = props => {

  var quantitiesInSale = [{}, {}]
  return (
    <>
      <h1>Add New Sale</h1>
      <Formik
        initialValues={{
          customerName: "",
          date: "",
          email: "",
          quantities: [] // added for our select
        }}
        validationSchema={Yup.object({
          customerName: Yup.string()
            .max(15, "Must be 15 characters or less")
            .required("Required"),
          date: Yup.date()
            .required("Required"),
          email: Yup.string()
            .email("Invalid email address"),
          quantities: Yup.array()
            .of(
              Yup.object().shape({
                name: Yup.string()
                  .required('Required'),
                qty: Yup.number()
                  .min(0, 'Must be atleast 1')
                  .required('Required'),
              })
            ).required('Must have products sold')
            .min(1, 'Minimum of 1 product'),
        })}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        <Form>
          <MyTextInput
            label="Customer Name"
            name="customerName"
            type="text"
            placeholder="John Doe"
          />
          <MyDateInput
            label="Date of Sale"
            name="date"
            type="date"
            placeholder={moment()}
          />
          <MyTextInput
            label="Customer Address"
            name="email"
            type="email"
            placeholder="jane@doe.com"
          />
          <h3>Add Products Bought</h3>
          <FieldArray
            name="quantities"
            render={arrayHelpers => (
              <div>
                {quantitiesInSale.length > 0 &&
                  quantitiesInSale.map((product, index) => (
                    <div className="row" key={index}>
                      <MyProductInput index={index}/>
                      <div className="col">
                        <button
                          className="secondary"
                          type="remove"
                          onClick={() => arrayHelpers.remove(index)}
                        >
                          X
                      </button>
                      </div>
                    </div>
                  ))}
                <button
                  type="add"
                  className="secondary"
                  onClick={() => arrayHelpers.push({ name: "", qty: "" })}
                >
                  Add Product
              </button>
              </div>
            )}
          />

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </>
  );
};

export default IMANewSaleForm;
