import fetch from 'isomorphic-unfetch'
import IMALayout from '../components/IMALayout';
import { ReactTabulator } from 'react-tabulator';
import inventoryStyles from 'inventoryStyles.js'


const InventoryPage = props => {
    return (
        <IMALayout>

            <style jsx global>
                {inventoryStyles}
            </style>
            <div className="container">
                Inventory management table.
                <ReactTabulator columns={props.columns} data={props.inventory} cellEdited={(cell) => console.log('cellEdited', cell)}
                    dataEdited={(newData) => console.log('dataEdited', newData)}
                    footerElement={<span>Footer</span>} />
            </div>
        </IMALayout>
    );
}

InventoryPage.getInitialProps = async () => {
    const res = await fetch('http://localhost:3000/api/inventory')
    const json = await res.json()
    console.log(`Inventory fetched.${JSON.stringify(json)}`);
    return {
        inventory: json,
        columns: [
            { title: "Product Name", field: "name", sorter: "string", width: 150 },
            { title: "Stock", field: "qty", sorter: "number" },
            { title: "Price", field: "price", align: "right" },
        ]
    }
}

export default InventoryPage;
