import withIMALayout from '../components/IMALayout'
import fetch from 'isomorphic-unfetch'

const Dashboard = () => (
    <div>
        <p>Hello Next.js</p>
        <label className="block">Days Ago</label>
        <input type="number" className="daysAgoInput" value={getInitialState.daysAgo} onChange={updateDaysAgoValue}></input>
        <button onClick={getSalesFromDaysAgo}>Get</button>
    </div>
);

Dashboard.getInitialProps = async () => {
    const res = await fetch('http://localhost:3000/api/sales?daysAgo=' + 30)
    const json = await res.json()
    console.log(`Show data fetched. ${JSON.stringify(json)}`);
    return { data: getMostRecentFormattedSales(json) };
}

Dashboard.getInitialState = async () => {
    return {
      daysAgo: 360
    };
  }

const updateDaysAgoValue = (evt) => {
    this.setState({
        daysAgo:  evt.target.value
    });
}

const getSalesFromDaysAgo = async () => {
    const res = await fetch('http://localhost:3000/api/sales?daysAgo=' + daysAgo)
    const json = await res.json()
    console.log(`Show data fetched. ${JSON.stringify(json)}`);
    return { data: getMostRecentFormattedSales(json) };
}

function getMostRecentFormattedSales(sales) {
    return sales;
};

export default withIMALayout(Dashboard);
