import withIMALayout from '../components/IMALayout'
import IMADaysAgoForm from '../components/IMADashBoardAgoForm'
import fetch from 'isomorphic-unfetch'
import useState from 'react'

const Dashboard = ({ }) => {
    let data = {
        days: {
          label: "Days",
          sales: 20,
          customers: 20,
        },
        months: {
          label: "Months",
          sales: 1,
          customers: 0,
        },
        years: {
          label: "Years",
          sales: 0,
          customers: 0,
        }
    }

    const [results, setResults] = useState(data);

    const onChange = (e) => {
        const data = { ...results };

        let name = e.target.name;

        let resultCollection = name.split(" ")[0].toLowerCase();
        let resultType = name.split(" ")[1].toLowerCase();

        data[resultCollection][resultType] = e.target.value;

        setResults(data);
    }

    return (
        <div>
            <IMADashBoardAgoForm collection="Sales" onChange={onChange} />
            <IMADashBoardAgoForm collection="Customers" onChange={onChange} />
        </div>
    )

};

Dashboard.getInitialProps = async () => {
    const res = await fetch('http://localhost:3000/api/sales?daysAgo=' + 30)
    const json = await res.json()
    console.log(`Show data fetched. ${JSON.stringify(json)}`);
    return { data: getMostRecentFormattedSales(json) };
}

const updateDaysAgoValue = (evt) => {
    this.daysAgo = evt.target.value;
}

